import React, { useState,useEffect } from 'react';
import { fb } from '../firebase';
import { generatePushId } from '../Helpers';
import { CollegesProviderValue } from "../Context";
import {auth} from '../firebase'

export const AddCollege = props => {
    const user = auth.currentUser;
    const { colleges, setColleges } = CollegesProviderValue();
    const [show, setShow] = useState(false);
    const [collegeName, setCollegeName] = useState("");


    const collegeID = generatePushId();
    const input = document.getElementById("input");

    const addCollege = () =>
      collegeName &&
      fb
        .firestore()
        .collection('colleges')
        .add({
          collegeID,
          name: collegeName,
          userID: user.uid,
        })
        .then(() => {
          setColleges([]);
          setCollegeName('');
          setShow(!show);
          input.value = "";
        });

        useEffect(() => {
          setShow(props.showState);
        }, [props.showState]);

    return (
      <>
      <div
        className="add-college-inner"
        onClick={() => {
          setShow(!show);
        }}
      >
        <span className="font-awsome">
          <i className="fas fa-plus"></i>
        </span>
        <span className="add-college">Add college</span>
      </div>

      <div className={`add-college-show ${show ? "show" : ""}`}>
        <div className="text-box">
          <div className="title">Add college</div>
          <div className="college-name">college Name</div>
          <input
            id="input"
            type="text"
            onChange={e => setCollegeName(e.target.value)}
          />
          <div className="buttons">
            <button
              className="add"
              onClick={() => {
                addCollege();
                props.value(false);
              }}
            >
              Add
            </button>
            <button
              className="cancel"
              onClick={() => {
                setShow(!show);
                props.value(false);
                input.value = "";
              }}
            >
              Cancel
            </button>
          </div>
        </div>
      </div>
    </>
    );
  };
