import React, { useState } from 'react';
import { SelectedCollegeProviderValue, CollegesProviderValue } from '../Context';
import { IndividualCollege } from './IndividualCollege';
import { useTasks } from "/Users/irenabrain/Documents/GitHub/CollegeChecklist/collegechecklist/src/Hooks";
import { CheckBox } from "/Users/irenabrain/Documents/GitHub/CollegeChecklist/collegechecklist/src/Components/Checkbox";


export const Colleges = () => {
  const { colleges } = CollegesProviderValue();
  const {
    selectedCollege,
    setSelectedCollege,
    setSelectedCollegeName
  } = SelectedCollegeProviderValue();
  const [active, setActive] = useState(false);
  const { tasks, archivedTasks } = useTasks(selectedCollege);
  return (
    <div className="colleges-inner">
      {colleges.map(college => ( 
        <li
          key={college.collegeID}
          className={active ? "active-sideMenue-college" : "sideMenue-college"}
          onClick={() => {
            setSelectedCollege(college.collegeID);
            setSelectedCollegeName(college.name);
            setActive(true);
          }}

        >
        {tasks.map(task => (
      <li key={task.id}>
        <CheckBox id={task.id} />
        <span> {task.tasks} </span>
      </li>
    ))}
          <IndividualCollege college={college} />
        </li>
      ))}
    </div> 
  );
};
